mapper.select:
select sca.id,CONCAT(st.nickname,'/',st.phone) as apply_person, c.`name` as apply_curriculum,c.id as apply_curriculum_id, IFNULL(SUM(cs.num*cs.unit_price),0) as total_price, sca.applicable_grade as grade,c.class_hours ,sca.apply_remark,sca.apply_time,sca.max_num,sca.class_place,sca.teacher_id,sca.begin_of_select_time, sca.end_of_select_time,sca.curr_start_time,sca.is_fine_quality from hklk_school_curriculum_apply sca LEFT JOIN hklk_curriculum c on sca.curriculum_id = c.id LEFT JOIN hklk_school_teacher st on sca.teacher_id = st.id LEFT JOIN hklk_consumables cs on sca.curriculum_id = cs.curriculum_id where sca.school_id = #{schoolId,jdbcType=INTEGER}
and sca.status in (1,3)
and sca.status = #{status,jdbcType=INTEGER}
and c.teacher_id != null
group by sca.id

SELECT sca.id, c.`name` AS curriculum_name, c.cover, sca.applicable_grade as grade, sca.curriculum_id, CONCAT(st.nickname,'/',st.phone) AS teacher_name, sca.curr_start_time, sca.max_num, sca.class_place, CONCAT(DATE_FORMAT(sca.begin_of_select_time,'%Y-%m-%d'),' 至 ',DATE_FORMAT(sca.end_of_select_time,'%Y-%m-%d')) as choose_time,sca.is_fine_quality, sca.status, (select count(1) from hklk_student_choice_curriculum scc where scc.sca_id = sca.id and scc.pay_state !=-1) AS student_num,
CASE WHEN sca.begin_of_select_time > SYSDATE() THEN 0/*未开课*/ WHEN sca.begin_of_select_time < SYSDATE() and SYSDATE() < sca.end_of_select_time THEN 1/*开始选课*/ WHEN sca.end_of_select_time < SYSDATE() and SYSDATE() < sca.curr_start_time THEN 2/*备课中*/ WHEN SYSDATE() > sca.curr_start_time and SYSDATE() < date_add( sca.curr_start_time, INTERVAL (SELECT CEILING(tc.class_hours/count(ts.id))*7 from hklk_curriculum tc LEFT JOIN hklk_school_curriculum_apply tsca on tc.id = tsca.curriculum_id left join hklk_school_syllabus ts on tsca.id = ts.sca_id where tsca.id = sca.id GROUP BY tsca.id) DAY ) THEN 3/*开始上课*/ WHEN SYSDATE() > date_add( sca.curr_start_time, INTERVAL (SELECT CEILING(tc.class_hours/count(ts.id))*7 from hklk_curriculum tc LEFT JOIN hklk_school_curriculum_apply tsca on tc.id = tsca.curriculum_id left join hklk_school_syllabus ts on tsca.id = ts.sca_id where tsca.id = sca.id GROUP BY tsca.id) DAY ) THEN 4/*已结束*/ END as apply_status,
concat(group_concat(h.week_type),'|',max(h.class_time)) class_time FROM hklk_school_curriculum_apply sca LEFT JOIN hklk_school_teacher st ON sca.teacher_id = st.id LEFT JOIN hklk_curriculum c ON sca.curriculum_id = c.id LEFT JOIN hklk_school_syllabus h on sca.id = h.sca_id WHERE sca.teacher_id = #{teacherId,jdbcType=INTEGER}
and sca.status = #{status,jdbcType=INTEGER}
and sca.status !=4
GROUP BY sca.id order by sca.apply_time desc

SELECT count(1) FROM ( SELECT sca.id, sca.is_fine_quality as isFineQuality, sca.applicable_grade AS grade, sca.curriculum_id as curriculumId, sca.status FROM hklk_school_curriculum_apply sca LEFT JOIN hklk_school_teacher st ON sca.teacher_id = st.id LEFT JOIN hklk_curriculum c ON sca.curriculum_id = c.id LEFT JOIN hklk_consumables cs on sca.curriculum_id = cs.curriculum_id WHERE sca.status = 3 AND sca.school_id = #{schoolId,jdbcType=INTEGER} /*查询精品*/
and sca.applicable_grade like CONCAT('%', #{grade}, '%')
/*查询精品*/ and sca.is_fine_quality = 1
/*查询热门课程*/ and sca.is_fine_quality = 0
GROUP BY sca.id ) h LEFT JOIN hklk_school_syllabus ss ON h.id = ss.sca_id
